import React, { useState } from "react";
import Trends from "../assets/Trends.png";

const ContactSection = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add form submission logic here, like sending the data to an API.
    console.log("Email:", email);
    console.log("Message:", message);
    // Reset the form fields after submission if needed
    setEmail("");
    setMessage("");
  };

  return (
    <>
      <section
        className="relative pt-64 text-center bg-center"
        style={{ backgroundImage: `url(${Trends})` }}
      >
        <h2 className="absolute inset-0 flex items-center justify-center text-6xl font-bold text-white">
          Contact
        </h2>
      </section>
      <div className="bg-transparent pt-[90px] pb-[116px] bg-slate-200">
        <div className="flex border border-black bg-slate-300">
          <div className=" border border-blue-500 pt-[55px] p-64 lg:px-[15px] md:w-full bg-slate-400">
            <h3 className="flex-1 text-lg font-semibold ">Contact Us</h3>
            <form onSubmit={handleSubmit} className="mt-4">
              <div className="mb-4">
                <label
                  htmlFor="email"
                  className="block text-sm font-medium text-gray-700"
                >
                  Email Address
                </label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  className="w-full p-2 mt-1 border border-gray-300 rounded-md"
                  placeholder="Enter your email"
                />
              </div>
              <div className="mb-4">
                <label
                  htmlFor="message"
                  className="block text-sm font-medium text-gray-700"
                >
                  Message
                </label>
                <textarea
                  id="message"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  required
                  rows="4"
                  className="w-full p-2 mt-1 border border-gray-300 rounded-md"
                  placeholder="Type your message here"
                />
              </div>
              <button
                type="submit"
                className="w-full py-2 text-white transition duration-200 bg-blue-600 rounded-md hover:bg-blue-700"
              >
                Submit
              </button>
            </form>
          </div>
          <div className="w-[210px] rounded-[10px] flex flex-wrap md:flex-col px-[93px] py-[30px] lg:px-[15px] md:w-full bg-red-500">
            <h3 className="text-lg font-semibold">Contact Information</h3>
            <p className="mt-2">Phone: (123) 456-7890</p>
            <p className="mt-2">Address: 123 Main St, City, Country</p>
          </div>
        </div>
      </div>
      <section></section>
    </>
  );
};

export default ContactSection;
