import React, { useState } from "react";
import blue from "../assets/blue-shirt.png"; // Import your local image
import black from "../assets/black-shirt.png"; // Import John Doe's image if needed

const CartSection = () => {
  const [quantities, setQuantities] = useState({ john: 1, jane: 1 }); // State to hold quantities
  const [selectedCountry, setSelectedCountry] = useState("");
  const [zipcode, setZipcode] = useState(""); // State for the zip code
  const [state, setState] = useState(""); // State for the state input

  // List of countries
  const countries = [
    "United States",
    "Canada",
    "United Kingdom",
    "Australia",
    "India",
  ];

  // Function to increase quantity
  const increaseQuantity = (product) => {
    setQuantities((prev) => ({ ...prev, [product]: prev[product] + 1 }));
  };

  // Function to decrease quantity
  const decreaseQuantity = (product) => {
    setQuantities((prev) => ({
      ...prev,
      [product]: Math.max(prev[product] - 1, 0), // Prevent negative quantity
    }));
  };

  const handleCountryChange = (event) => {
    setSelectedCountry(event.target.vaderlue);
  };

  const handleZipcodeChange = (event) => {
    setZipcode(event.target.value);
  };

  const handleStateChange = (event) => {
    setState(event.target.value);
  };

  return (
    <form>
      <div>
        <div className="flex justify-between  mt-14 h-[25cm]">
          <div className="m-10 w-[17cm] ml-56 mb-[6.5cm] h-[13cm] rounded-xl">
            <table className="w-full h-[10cm] border border-collapse border-gray-400">
              <thead>
                <tr>
                  <th className="p-2 border-b border-gray-400">PRODUCT</th>
                  <th className="p-2 border-b border-gray-400">PRICE</th>
                  <th className="p-2 border-b border-gray-400">QUANTITY</th>
                  <th className="p-2 border-b border-gray-400">TOTAL</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="p-2 border-b border-gray-400">1</td>
                  <td className="p-2 border-b border-gray-400">John Doe</td>
                  <td className="p-2 border-b border-gray-400">
                    Some details about John
                  </td>
                  <td className="p-2 border-b border-gray-400">
                    Some details about John
                  </td>
                </tr>
                <tr>
                  <td className="px-4 py-2 border">2</td>
                  <td className="px-4 py-2 border">
                    <img
                      src={black} // Use the imported local image
                      alt="Jane Smith"
                      className="inline-block w-12 h-12 mr-2" // Tailwind classes for size and spacing
                    />
                    Jane Smith
                  </td>
                  <td className="px-4 py-2 border">Details about Jane</td>
                  <td className="px-4 py-2 border">Some details about John</td>
                </tr>
              </tbody>
            </table>
            <div className="border border-gray-500 h-[3cm]"></div>
          </div>
          <div className="m-10 border border-gray-300 w-[15cm] h-[20cm] mr-[6cm] rounded-md p-10 ">
            <h2 className="mt-5 ml-10">CART TOTALS</h2>
            <div className="flex">
              <h3 className="m-2 ml-10 text-lg text-gray-600">Subtotals:</h3>
              <div className="m-2 text-lg text-gray-600 ml-14">$6266</div>
            </div>
            <hr></hr>
            <div className="flex h-[100px] w-[]">
              <h3 className="w-auto m-2 mt-6 ml-10 text-lg text-gray-600">
                Shipping:
              </h3>
              <div>
                <div className="flex flex-col items-center h-[400px] ml-10 text-[125%] bg p-6 pr-12">
                  <div className="">
                    <p className="text-lg text-gray-600">
                      There are no shipping methods available. Please double
                      check your address, or contact us if you need any help.
                    </p>
                  </div>
                  <div className="">
                    <p className="m-2 ml-10 text-lg text-gray-600">
                      CALCULATE SHIPPING
                    </p>
                    <select
                      value={selectedCountry}
                      onChange={handleCountryChange}
                      className="p-2 border border-gray-300 rounded-md "
                    >
                      <option value="">-- Choose a Country --</option>
                      {countries.map((country, index) => (
                        <option key={index} value={country}>
                          {country}
                        </option>
                      ))}
                    </select>
                    {/* Text Box for State */}
                    <input
                      type="text"
                      value={state}
                      onChange={handleStateChange}
                      placeholder="Enter State"
                      className="w-full p-2 mt-4 text-lg text-gray-600 border border-gray-300 rounded-md"
                    />
                    {/* Text Box for Zip Code */}
                    <input
                      type="text"
                      value={zipcode}
                      onChange={handleZipcodeChange}
                      placeholder="Enter Zip Code"
                      className="w-full p-2 mt-4 border border-gray-300 rounded-md"
                    />
                    <button className="w-auto px-6 py-2 mt-5 text-lg font-semibold text-gray-600 transition duration-300 ease-in-out bg-gray-200 rounded-full shadow hover:bg-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75">
                      Update Total
                    </button>
                  </div>
                  <div className="p-2 mt-10 m-0 w-[8cm] text-lg font-semibold text-gray-600 transition duration-300 ease-in-out bg-blue-500 shadow rounded-xl hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75">
                    <button>Proceed to Checkout</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </form>
  );
};

export default CartSection;
